using UnityEngine;public class playerMovement : MonoBehaviour {    private Vector3 moveVector;    [SerializeField] private float speed = 5;    private float verticalVelocity;    private Rigidbody body;    private float camAnimationDuration;    public GameManager gameManager;    private Animator anim;    public float knockbackForce, knockbackTime;    private float knockbackCounter;    private bool rightFoot, subDominant;    public bool isAlive;    // Start is called before the first frame update    void Start() {        body = GetComponent<Rigidbody>();        camAnimationDuration = GameObject.Find("Main Camera").GetComponent<cameraMovement>().animationDuration;        anim = GetComponent<Animator>();        anim.SetFloat(Tags.ANIMATION_CONDITION_FLOAT_SPEED, speed / 10);    }    // Update is called once per frame    void Update() {        if (Time.time < camAnimationDuration) {            body.velocity = Vector3.forward * speed;            return;        }        else if (knockbackCounter <= 0) {            verticalVelocity = -0.5f;            moveVector = Vector3.zero;            // X - Moving left or right            if (Application.isMobilePlatform) {                moveVector.x = Input.acceleration.x * 2 * speed; // Mobile Controls            }            else {                moveVector.x = Input.GetAxis("Horizontal") * speed; // PC Controls             }            //Y - Up and Down            moveVector.y = verticalVelocity;            // Z- Forward or Backward            moveVector.z = speed;            body.velocity = moveVector;        }        else {            knockbackCounter -= Time.deltaTime;        }            }    public void IncreaseSpeed(int increment) {        speed += increment;        anim.SetFloat(Tags.ANIMATION_CONDITION_FLOAT_SPEED, speed / 10);    }    public void Knockback(Vector3 direction) {        // Knockback simulation for Later Implementation        knockbackCounter = knockbackTime;        body.velocity = direction * knockbackForce;    }    public void Death() {        speed = 0;        anim.SetTrigger(Tags.ANIMATION_CONDITION_TRIGGER_DEATH);        isAlive = false;    }    public void GameOver() {        gameManager.GameOver();    }  ///////////////////////////////Triggered in Animation///////////////////////////////////    public void DisableJump() {        anim.SetBool(Tags.ANIMATION_CONDITION_BOOL_JUMP, false);    }    public void DisableKick() {        anim.SetBool(Tags.ANIMATION_CONDITION_BOOL_KICK, false);    }    public void DisableSlide() {        anim.SetBool(Tags.ANIMATION_CONDITION_BOOL_SLIDE, false);    }}